import * as React from "react";
import { makeStyles } from "@material-ui/core";

export const useStyles = makeStyles(theme => ({
  titleText: {
    opacity: '0',
    '&:hover': {
      cursor: 'pointer',
      transition: 'all .5s ease-in-out',
      transform: 'scale(1.1)'
   }
  },
  cardImage: {
    '&:hover': {
      cursor: 'pointer',
      transition: 'all .5s ease-in-out',
      transform: 'scale(2)',
      opacity: '0'
   }
  }

}));
